function toggleBurger(){const e=document.querySelector("#burger");e.addEventListener("click",(function(){e.classList.toggle("open")}))}function showNotification(){const e=document.querySelector(".cookie-notif__modal"),t=document.querySelectorAll(".cookie-notif__close");window.addEventListener("DOMContentLoaded",(function(){e&&"shown"!=sessionStorage.getItem("cookieState")?e.classList.add("show","fade"):"shown"==sessionStorage.getItem("cookieState")&&e.classList.add("hide")}));for(let o=0;o<t.length;o++)t[o].addEventListener("click",(function(){e.classList.remove("show"),sessionStorage.setItem("cookieState","shown")})),t[o].addEventListener("click",(function(){e.addEventListener("transitionend",(function(){e.classList.add("hide")}))}))}toggleBurger(),showNotification();